import { createSlice } from "@reduxjs/toolkit";
import { uiActions } from "./ui-slice";

const cartSlice = createSlice({
  name: 'cart',
  initialState: {
    items: [],
    totalQuantity: 0,
    totalAmount: 0,
  },
  reducers: {
    addItemToCart(state, action) {
      const newItem = action.payload;
      const existingItem = state.items.find(item => item.id === newItem.id);
      state.totalQuantity++;
      
      if (!existingItem) {
        state.items.push({
          id: newItem.id,
          price: newItem.price,
          quantity: 1,
          totalPrice: newItem.price, // Use totalPrice (capital 'P')
          name: newItem.title
        });
      } else {
        existingItem.quantity++;
        existingItem.totalPrice = existingItem.totalPrice + newItem.price; // Use totalPrice (capital 'P')
      }
      state.totalAmount += newItem.price; 
    },

    removeItemFromCart(state, action) {
      const id = action.payload;
      const existingItem = state.items.find(item => item.id === id);
      state.totalQuantity--;
      state.totalAmount -= existingItem.price;
      if (existingItem.quantity === 1) {
        state.items = state.items.filter(item => item.id !== id);
      } else {
        existingItem.quantity--;
        existingItem.totalPrice = existingItem.totalPrice - existingItem.price;
      }
      
    }
  }
});


const sendCartData = (cart) => {
  return async(dispatch) => {
    dispatch(uiActions.showNotification({
      status: 'pending',
      title:'Sending...',
      message: 'Sending cart data!',
    }));   

    const sendRequest = async () => {
      const response = await fetch('https://just-project-1400b-default-rtdb.firebaseio.com/cart.json',{ method: 'PUT', body: JSON.stringify(cart), });

      if(!response.ok) {
        throw new Error('Sending cart data failed');
      } 
    };
    
    
    
    dispatch(
      uiActions.showNotification({
        status:'success',
        title:'Success!',
        message: 'Sent cart data successfully!'
      })
    );    
  }
}

export const CartActions = cartSlice.actions;

export default cartSlice;
